import React, { useState } from "react";

import NewExpenseForm from "./NewExpenseForm";

import "./NewExpense.css";

const NewExpense = (props) => {

    const [openExpenseForm, setOpenExpenseForm] = useState(false);
    const [cancleExpenseForm, setCancleExpenseForm] = useState(false);

    const saveExpenseDataHandler = (enteredExpenseData) => {

        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString(),
        }
        console.log(expenseData);
        props.onAddExpense(expenseData);
    }

    const closeExpenseFormHandler = (close) => {
        setCancleExpenseForm(close);
        console.log(cancleExpenseForm);
        console.log(close);
    }

    const opneExpenseFormContainer = () => {
        if(cancleExpenseForm === true) {
            setOpenExpenseForm(false);
        } else {
            setOpenExpenseForm(true);
        }

    }

    if (openExpenseForm === true) {
        return (
            <div className="new-expense">
                <NewExpenseForm onSaveExpensedata={saveExpenseDataHandler} onCancleForm={closeExpenseFormHandler} />
            </div>
        );
    }

    return (
        <div className="new-expense">
            <div className="new-expense__actions">
                <button type="" onClick={opneExpenseFormContainer}>Add New Expenses</button>
            </div>
        </div>
    );
}
export default NewExpense;
