import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";

import "./Expenses.css";

const Expenses = (props) => {

    const [filterData, setFilterData] = useState('2020');

    const filterHandler = (selectedFilterValue) => {
        console.log(selectedFilterValue);
        setFilterData(selectedFilterValue);
    }

    const filterExpenseData = props.expenses.filter( expenseItems => {
        return ( expenseItems.date.getFullYear().toLocalString() === filterData);
    })

    return (
        <Card className="expenses">
            <ExpenseFilter selectedYear={filterData} onFilterChange={filterHandler} />
            {filterExpenseData.map(items => {
                return (
                    <ExpenseItem
                        key={items.id}
                        title={items.title}
                        amount={items.amount}
                        date={items.date}
                    />);
            })}
        </Card>
    );
}

export default Expenses;
