import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";

import "./Expenses.css";

const Expenses = (props) => {

    const [ filterData, setFilterData ] = useState('2020');

    const filterHandler = (selectedFilterValue) => {
        console.log(selectedFilterValue);
        setFilterData(selectedFilterValue);
    }

    return (
        <Card className="expenses">
            <ExpenseFilter selectedYear={filterData} onFilterChange={filterHandler} />
            {props.expenses.map(items =>{
                <ExpenseItem title={items.title} date={items.date} amount={items.amount}/>
            })}
            <ExpenseItem
                title={props.expenses[0].title}
                date={props.expenses[0].date}
                amount={props.expenses[0].amount}
            >
            </ExpenseItem>
            <ExpenseItem
                title={props.expenses[1].title}
                date={props.expenses[1].date} amount={props.expenses[1].amount}
            >
            </ExpenseItem>
            <ExpenseItem
                title={props.expenses[2].title}
                date={props.expenses[2].date}
                amount={props.expenses[2].amount}
            >
            </ExpenseItem>
            <ExpenseItem
                title={props.expenses[3].title}
                date={props.expenses[3].date}
                amount={props.expenses[3].amount}
            >
            </ExpenseItem>
        </Card>
    );
}

export default Expenses;
